   PGGA = (typeof PGGA !== "undefined") ? PGGA : {};
	PGGA.isStoryPage= function(){
	    var story_domains = ["dev.post-gazette.com","www.post-gazette.com"];
	    return story_domains.some(function(needle){return window.location.hostname.indexOf(needle) >= 0}) >= 0 && window.location.href.search("/stories/") >= 0
	};
	PGGA.isHomePage = function(){
	    var allowed_urls = ["//www.post-gazette.com/","//dev.post-gazette.com/"];
	    return allowed_urls.some(function(needle){ return window.location.href.split("?")[0].indexOf(needle) >= 0});
	};
	PGGA.isSectionFront = function(){
	   const regex = /https?:\/\/(www|dev|stage)\.post-gazette.com\/(\w+)(\/?)(\w+)?(\/?)/g;
	   var loc = window.location.href;
	   var res = loc.match(regex);
	   if(res && res.length == 1){
	     var test = res[0]
	     return !((test.length+1) < loc.length)
	   }
	   return false;
	};
	PGGA.setLoadData = function(){
		var PGSITECODE = PGVARS.sitetype;
        if(PGGA.isHomePage()){
            PGGA.clickLabel=PGSITECODE+" Home Page Classifieds Test V6.2";
